import React, { useState } from "react";
import { quizData } from "./quizData";
import { Button } from "../button/";
import "./Quiz.css";
import "../../Shared.css";

const Quiz = () => {
  const createStateStore = (arr: any) => {
    const store: any = [];

    for (let i = 0; i < arr.length; i++) {
      store[arr[i].genre] = false;
    }
    return store;
  };

  const quizState = createStateStore(quizData);

  const [genreState, changeGenreState] = useState(quizState);

  console.log("GENRE", genreState);

  const onGenreClick = (name: string) => {
    const newState: any = [...genreState];
    newState[name] = !newState[name];
    console.log("new", newState)
    changeGenreState(newState);
    console.log("STATe", genreState);
  };

  const renderGenres = (genres: { genre: string }[]) => {
    return genres.map((item, index: number) => (
      <Button
        key={index}
        type={"quiz-empty"}
        title={item.genre}
        action={onGenreClick}
      />
    ));
  };

  return (
    <div className="quiz">
      <div className="heading__primary">Before We Start!</div>
      <div className="heading__secondary">
        Please choose up to three of your favorite genres.{" "}
      </div>
      <div className="quiz__genres">{renderGenres(quizData)}</div>
      <Button
        type={"primary"}
        title={"I'm ready!"}
        action={() => onGenreClick("Rap")}
      />
    </div>
  );
};

export default Quiz;
