import React, { useState, Fragment } from "react";
import { updateStorageData } from "../../utilities/localStorage";
import gql from "graphql-tag";
import { Query } from "react-apollo";
import { SwipeCard } from "./swipeCard";
import { Button } from "../../components/button";
import "../../Shared.css";
import "./Dashboard.css";

const TRACKS_QUERY = gql`
  query TracksQuery($selectedGenres: [String!]!) {
    suggestedTracks(selectedGenres: $selectedGenres) {
      id
      name
      artists {
        name
      }
      preview_url
      album {
        images {
          url
        }
      }
    }
  }
`;

const Dashboard = props => {
  const [currentIndex, updateIndex] = useState(0);

  const nextSong = () => {
    updateIndex(currentIndex + 1);
    document.querySelector(".card__video").load();
  };

  const previousSong = () => {
    updateIndex(currentIndex - 1);
    document.querySelector(".card__video").load();
  };

  const renderArtists = arr => {
    return arr.map((artist, index) => {
      if (index === arr.length - 1) return artist.name;
      return artist.name + ", ";
    });
  };

  console.log("props", props.location.state);
  return (
    <div className="d-flex justify-content-center align-items-center h-100 flex-column">
      <Query
        query={TRACKS_QUERY}
        variables={{ selectedGenres: props.location.state }}
      >
        {({ loading, error, data }) => {
          if (loading) return <h4>LOADING...</h4>;
          if (error) console.log("error", error);
          else {
            console.log("DATA", data);
            return (
              <Fragment>
                <SwipeCard
                  nextSong={() => nextSong()}
                  previousSong={() => previousSong()}
                  image={data.suggestedTracks[currentIndex].album.images.url}
                />
                <div className="card__heading-wrapper mb-2 mt-2">
                  <div className="heading__primary m-1 text-center">
                    {data.suggestedTracks[currentIndex].name}
                  </div>
                  <div className="heading__secondary mt-2 text-white">
                    {renderArtists(data.suggestedTracks[currentIndex].artists)}
                  </div>
                  <video className="card__video" controls name="media">
                    <source
                      src={`${data.suggestedTracks[currentIndex].preview_url}`}
                      type="audio/mpeg"
                    />
                  </video>
                </div>

                <Button
                  type={"primary"}
                  title={"Add to playlist"}
                  action={() => 
                    updateStorageData(
                      "saved_tracks",
                      data.suggestedTracks[currentIndex].id
                    )
                  }
                  fill={"rgb(255, 78, 80)"}
                />
              </Fragment>
            );
          }
        }}
      </Query>
    </div>
  );
};

export default Dashboard;
