import React from "react";

interface CardBodyProps {
  name: string;
  artists: { name: string }[];
  preview_url: string;
  style?: {name?: string, artists?: string, audioBar?: string; };
}
export const CardBody = ({ name, artists, preview_url, style }: CardBodyProps) => {
  const renderArtists = (arr: { name: string }[]) => {
    return arr.map((artist: { name: string }, index: number) => {
      if (index === arr.length - 1) return artist.name;
      return artist.name + ", ";
    });
  };
  return (
    <div className="card__heading-wrapper mb-2 mt-2">
      <div className={style ? style.name : "heading__primary m-1 text-center" }>{name}</div>
      <div className="heading__secondary mt-2 text-white">
        {renderArtists(artists)}
      </div>
      <video className="card__video" controls>
        <source src={`${preview_url}`} type="audio/mpeg" />
      </video>
    </div>
  );
};
