import React from "react";
import {
  SAVED_TRACKS_ERROR,
  UNAUTHORIZED,
  USER_DATA_ERROR
} from "../utilities/errorTypes";

interface QueryErrorProps {
  errorMessage: string;
  pushHistory: ({}) => void;
}

export const QueryError = ({ errorMessage, pushHistory }: QueryErrorProps) => {
  const renderNoTracksMessage = () => (
    <div className="heading__primary content-wrapper d-flex justify-content-center">
      You haven't saved any tracks yet.
    </div>
  );
  const determineError = (errorMessage: string) => {
    console.log("ERR", errorMessage);
    switch (errorMessage) {
      case SAVED_TRACKS_ERROR:
        console.log("yeah2");
        return renderNoTracksMessage();
      case UNAUTHORIZED:
        console.log("yeah");
        pushHistory({
          pathname: "/",
          state: { authError: true }
        });
        return null;
      case USER_DATA_ERROR:
        console.log("yeah");
        pushHistory({
          pathname: "/",
          state: { authError: true }
        });
        return null;

      default:
        console.log("yeah1");
        return (
          <div>There was a problem with our server. We are working on it.</div>
        );
    }
  };

  return determineError(errorMessage);
};
