import React from "react";
import "./Alert.css";

interface MessageProps {
  message: string;
}

export const SuccessMessage = ({ message }: MessageProps) => {
  return (
    <div className="message__success d-flex justify-content-center flex-column align-items-center p-3">
      <h5 className="text-white bg-transparent">Success!</h5>
      <div className="text-white bg-transparent">{message}</div>
    </div>
  );
};

export const ErrorMessage = ({ message }: MessageProps) => {
  return (
    <div className="message__error d-flex justify-content-center flex-column align-items-center p-3">
      <h5 className="text-white bg-transparent">Error!</h5>
      <div className="text-white bg-transparent">{message}</div>
    </div>
  );
};
