import React from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import ApolloClient from "apollo-boost";
import { ApolloProvider } from "react-apollo";
import Header from "./components/header";
import Quiz from "./pages/quiz";
import Dashboard from "./pages/dashboard/index.js";
import SavedSongs from "./pages/savedSongs";
import { getStorageData, updateStorageData } from "./utilities/localStorage";
import gql from "graphql-tag";
import "./App.css";

const client = new ApolloClient({
  uri: "http://localhost:5000/graphql",
  headers: {
    token: getStorageData("token")
  }
});

const USER_DETAILS_QUERY = gql`
  query UserDetails {
    userDetails {
      display_name
    }
  }
`;

const App = () => {
  const queryUser = async () => {
    try {
      const result = await client.query({
        query: gql`
          {
            userDetails {
              display_name
            }
          }
        `
      });
      console.log("result1", result.data);
      const username = result.data.userDetails.display_name;
      updateStorageData("username", username);
    } catch (err) {
      console.log("ERROR", err);
    }
  };

  queryUser();

  return (
    <ApolloProvider client={client}>
      <BrowserRouter>
        <Switch>
          {/* <Header /> */}
          <Route path="/" exact component={Quiz} />
          <Route path="/dashboard" component={Dashboard} />
          <Route path="/saved" component={SavedSongs} />
        </Switch>
      </BrowserRouter>
    </ApolloProvider>
  );
};

export default App;
