import React from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import ApolloClient from "apollo-boost";
import { ApolloProvider, Query } from "react-apollo";
import Header from "./components/header";
import Quiz from "./pages/quiz";
import Dashboard from "./pages/dashboard/index.js";
import SavedSongs from "./pages/savedSongs";
import { getStorageData } from "./utilities/localStorage";
import gql from "graphql-tag";
import "./App.css";

const client = new ApolloClient({
  uri: "http://localhost:5000/graphql",
  headers: {
    token: getStorageData("token")
  }
});

const USER_DETAILS_QUERY = gql`
  query UserDetails {
    userDetails {
      display_name
    }
  }
`;

const App = ({}) => {
  const queryUser = async () => {
    try {
      const result = await client.query({
        query: gql`
          {
            userDetails {
              display_name
            }
          }
        `
      });
      return result.data.userDetails.display_name;
    } catch (err) {
      console.log("ERROR", err);
    }
  };

  return (
    <ApolloProvider client={client}>
      {/* <Query query={USER_DETAILS_QUERY}>
        {(props: any) => {
          if (props.loading) return <div>Loading...</div>;
          if (props.error) console.log("error", props.error);
          else {
            console.log("THE DATA", props.data.userDetails.display_name);
            return (
              <div className="d-flex justify-content-center m-5">
                <select className="select-input">
                  <option>{props.data.userDetails.display_name}</option>
                </select>
              </div>
            );
          }
        }}
      </Query> */}
      {queryUser()}
      <BrowserRouter>
        <Switch>
          {/* <Header /> */}
          <Route path="/" exact component={Quiz} />
          <Route path="/dashboard" component={Dashboard} />
          <Route path="/saved" component={SavedSongs} />
        </Switch>
      </BrowserRouter>
    </ApolloProvider>
  );
};

export default App;
