import React, { useState } from "react";
import { quizData } from "./quizData";
import { Button } from "../button/";
import "./Quiz.css";
import "../../Shared.css";

const Quiz = () => {
  const createStateStore = (arr: any) => {
    const store = new Array();
    store.fill(0);

    for (let i of arr) {
      store[i] = true;
    }
    return store;
  };

  const [genreState, changeGenreState] = useState();

  const renderGenres = (genres: { genre: string }[]) => {
    return genres.map((item, index: number) => (
      <Button key={index} type={"quiz-empty"} title={item.genre} />
    ));
  };

  return (
    <div className="quiz">
      <div className="heading__primary">Before We Start!</div>
      <div className="heading__secondary">
        Please choose up to three of your favorite genres.{" "}
      </div>
      <div className="quiz__genres">{renderGenres(quizData)}</div>
      <Button type={"primary"} title={"I'm ready!"} />
    </div>
  );
};

export default Quiz;
