const TRACK_DATA = "track_data";

export const getStorageData = (key: string) => {
  try {
    const token = localStorage.getItem(key);
    return token;
  } catch {
    return null;
  }
};

export const updateStorageData = (data: string, key: string) => {
  try {
    const storageData = getStorageData(key);

    let stringifiedData;
    if (storageData) {
      const parsedData = JSON.parse(storageData);
      parsedData.push(data);
      stringifiedData = JSON.stringify(parsedData);
      localStorage.setItem(key, stringifiedData);
      return stringifiedData;
    }

    stringifiedData = JSON.stringify(data);
    localStorage.setItem(key, stringifiedData);
    return stringifiedData;
  } catch (err) {
    console.log("ERR", err);
    return err;
  }
};

export const removeStorageData = () => {
  try {
    localStorage.removeItem(TRACK_DATA);
    return;
  } catch {
    return null;
  }
};
